mapper.select:
select
sl.*,
sl.id as log_id
from sys_log sl

    sl.is_delete = 0

        and sl.type = #{dto.logType.code}


        and sl.event = #{dto.event}


       and sl.create_user_code like concat('%', #{dto.createUserCode}, '%')


       and sl.create_time between #{dto.startCreateTime} and #{dto.endCreateTime}


        and sl.id in

            #{id}


    order by sl.create_time desc

select sl.id online_id, sl.create_user_code username, sl.create_user_name real_name, sl.ip_address, sl.os, sl.browser, sl.create_time login_time from sys_log sl where sl.id in ( select max(id) from sys_log where is_delete = 0 and create_time > #{dto.startCreateTime} group by create_user_code ) and sl.event = #{dto.event}
and sl.create_user_code like concat('%', #{dto.username}, '%')
order by sl.create_time desc
